master_programs.admission_requirements:
const generalRequirements = [
    "ألا يقل تقدير الطالب في الشهادة الجامعية الأولى عن جيد أو ما يعادله حداً أدنى.",
    "أن تكون دراسة الطالب في مرحلة البكالوريوس بالانتظام ومن جامعة معتمدة ومعترف بها.",
    "التقدم لامتحان قدرات اللغة الأجنبية وفق الأسس المعتمدة من وزارة التعليم العالي والبحث العلمي الأردنية (IELTS, TOEFL iBT, أو فرنسي/ألماني).",
    "في حال عدم اجتياز امتحان اللغة، يلتزم الطالب بدراسة برنامج تأهيلي (6 ساعات معتمدة) في الفصل الأول، ويشترط النجاح فيه بمعدل (50%-75%) حسب التخصص.",
    "في حال اختلاف تخصص البكالوريوس عن الماجستير، يتم تحديد مواد استدراكية بحد أقصى (9) ساعات معتمدة.",
    "يجوز للطالب الوافد الحاصل على دبلوم عالي بتقدير لا يقل عن جيد جداً الالتحاق بالبرنامج بغض النظر عن تقدير البكالوريوس.",
    "لا يسمح لطلبة الدبلوم المهني أو الفني الالتحاق ببرامج الدراسات العليا.",
    "لا يجوز احتساب مواد من برنامج الدبلوم العالي عند التحاق الطالب في برنامج الماجستير."
];
        // بيانات التخصصات (من MasterSubLevelSelection)
        const masterFaculties = [
            {
                id: "master-law",
                name: "كلية الحقوق",
                courses: [
                    { id: "law-private",  name: "القانون الخاص", description: "برنامج ماجستير متخصص في القانون الخاص", credits: 33, duration: "سنتان", level: "ماجستير", requirements: generalRequirements },
                    { id: "law-public", name: "القانون العام", description: "برنامج ماجستير متخصص في القانون العام", credits: 33, duration: "سنتان", level: "ماجستير", requirements: generalRequirements },
                ],
            },
            {
                id: "master-business",
                name: "كلية الأعمال",
                courses: [
                    { id: "mba",  name: "إدارة الأعمال", description: "برنامج ماجستير شامل في إدارة الأعمال", credits: 36, duration: "سنتان", level: "ماجستير", requirements: generalRequirements },
                    { id: "master-accounting",  name: "المحاسبة", description: "برنامج ماجستير متخصص في المحاسبة", credits: 33, duration: "سنتان", level: "ماجستير", requirements: generalRequirements },
                ],
            },
            {
                id: "master-media",
                name: "كلية الإعلام",
                courses: [
                    { id: "master-media", name: "الإعلام", description: "برنامج ماجستير في الإعلام الرقمي", credits: 33, duration: "سنتان", level: "ماجستير", requirements: generalRequirements },
                ],
            },
            {
                id: "master-pharmacy",
                name: "كلية الصيدلة",
                courses: [
                    { id: "master-pharmacy",  name: "العلوم الصيدلانية", description: "برنامج ماجستير في العلوم الصيدلانية", credits: 33, duration: "سنتان", level: "ماجستير", requirements: generalRequirements },
                ],
            },
        ];

        // بيانات الأسعار
        const pricingData = [
            { courseId: "law-private", basePriceJOD: 140, basePriceUSD: 200 },
            { courseId: "law-public", basePriceJOD: 140, basePriceUSD: 200 },
            { courseId: "mba", basePriceJOD: 200, basePriceUSD: 282 },
            { courseId: "master-accounting", basePriceJOD: 150, basePriceUSD: 215 },
            { courseId: "master-media", basePriceJOD: 180, basePriceUSD: 255 },
            { courseId: "master-pharmacy", basePriceJOD: 180, basePriceUSD: 255 }
        ];

        let isFeesUSD = false;
        function toggleFeesCurrency() {
            isFeesUSD = !isFeesUSD;
            const btnText = document.getElementById('currencyBtnText');
            if(btnText) btnText.textContent = isFeesUSD ? 'عرض بالدينار' : 'عرض بالدولار';

            document.querySelectorAll('.fee-price').forEach(el => {
                const jod = el.getAttribute('data-jod');
                const usd = el.getAttribute('data-usd');
                if (jod && usd) {
                    el.textContent = isFeesUSD ? `${usd} دولار` : `${jod} دينار`;
                }
            });
        }

        function initPage() {
            const urlParams = new URLSearchParams(window.location.search);
            const courseId = urlParams.get('id') || 'mba'; // افتراضي

            let data = null;
            // البحث عن التخصص في الكليات
            for (const faculty of masterFaculties) {
                const found = faculty.courses.find(c => c.id === courseId);
                if (found) {
                    data = found;
                    break;
                }
            }

            if (data) {
                // تحديث النصوص
                document.getElementById('tabTitle').textContent = data.name;
                document.getElementById('pageTitle').textContent = data.name;
                document.getElementById('heroCredits').innerHTML = `<i data-lucide="book-open" class="w-5 h-5"></i> ${data.credits} ساعة`;
                document.getElementById('heroDuration').innerHTML = `<i data-lucide="clock" class="w-5 h-5"></i> ${data.duration}`;
                document.getElementById('heroLevel').textContent = data.level;
                document.getElementById('courseDescription').textContent = data.description;
                document.getElementById('infoLevel').textContent = data.level;
                document.getElementById('infoCredits').textContent = data.credits + " ساعة";

                // تحديث الرسوم
                const priceInfo = pricingData.find(p => p.courseId === courseId);
                if (priceInfo) {
                    document.getElementById('priceJOD').textContent = priceInfo.basePriceJOD;
                    document.getElementById('priceUSD').textContent = `$${priceInfo.basePriceUSD}`;
                } else {
                    document.getElementById('priceJOD').textContent = "--";
                    document.getElementById('priceUSD').textContent = "--";
                }

                // المتطلبات
                const reqList = document.getElementById('requirementsList');
                reqList.innerHTML = data.requirements.map(req => `
                    <li class="flex gap-3 p-3 bg-white/50 rounded-xl">
                        <i data-lucide="check" class="text-green-600 shrink-0"></i>
                        <span>${req}</span>
                    </li>
                `).join('');
            }

            // تشغيل الأيقونات
            lucide.createIcons();
        }

        document.addEventListener('DOMContentLoaded', initPage);

